parameters.functionCode:
const payload = items[0]?.json ?? {};
const rawItems = Array.isArray(payload.items) ? payload.items : (Array.isArray(payload) ? payload : []);
const lookbackDays = Number($env.DIGEST_LOOKBACK_DAYS ?? 5);
const maxItems = Number($env.DIGEST_MAX_ITEMS ?? 7);
const threshold = new Date(Date.now() - lookbackDays * 86400000);

const normalized = rawItems
  .map(item => ({
    title: item.title || item.subject || 'Untitled',
    url: item.url || item.link || null,
    summary: item.summary || item.description || '',
    published_at: item.published_at || item.date || new Date().toISOString()
  }))
  .filter(item => {
    try {
      const published = new Date(item.published_at);
      return !isNaN(published) && published >= threshold;
    } catch (e) {
      return true;
    }
  })
  .slice(0, maxItems);

return [{
  json: {
    items: normalized,
    generated_at: new Date().toISOString(),
    lookback_days: lookbackDays
  }
}];

const subscriber = $json;
const feed = $('2. Normalize Feed').item.json;
const items = Array.isArray(feed.items) ? feed.items : [];
const lang = subscriber.preferred_lang || 'ru';
const unsubscribeBase = $env.NEWSLETTER_UNSUB_BASE_URL || 'https://example.com/newsletter/unsubscribe';
const unsubscribeUrl = `${unsubscribeBase}?token=${encodeURIComponent(subscriber.unsubscribe_token)}&action=unsubscribe`;
const introByLang = {
  ru: 'Свежие материалы за последние дни:',
  en: 'Fresh updates for you:',
  pl: 'Najnowsze materiały dla Ciebie:'
};
const outroByLang = {
  ru: `Можно управлять подпиской: ${unsubscribeUrl}`,
  en: `Manage your subscription: ${unsubscribeUrl}`,
  pl: `Zarządzaj subskrypcją: ${unsubscribeUrl}`
};

const bulletList = items.map((item, idx) => {
  const index = idx + 1;
  const title = item.title || `Материал ${index}`;
  const url = item.url ? `${index}. ${title} — ${item.url}` : `${index}. ${title}`;
  return url;
}).join('
');

const subjectPrefix = $env.NEWSLETTER_DIGEST_SUBJECT_PREFIX || '[Digest]';
const subject = `${subjectPrefix} ${feed.generated_at?.slice(0, 10) || ''}`.trim();

const bodyText = `${introByLang[lang] || introByLang.en}

${bulletList}

${outroByLang[lang] || outroByLang.en}`;

return [{
  json: {
    email: subscriber.email,
    full_name: subscriber.full_name,
    subject,
    body_text: bodyText,
    body_html: bodyText.replace(/
/g, '<br />'),
    unsubscribe_url: unsubscribeUrl,
    subscriber_id: subscriber.subscriber_id,
    items_sent: items.length
  }
}];

parameters.query:
INSERT INTO messages (conversation_id, direction, medium, sender_contact_id, body, body_html, message_ts, meta)
VALUES (NULL, 'outbound', 'email', NULL, $1, $2, NOW(), jsonb_build_object('kind', 'newsletter_digest', 'subscriber_id', $3::uuid, 'email', $4, 'subject', $5))
RETURNING id;

INSERT INTO pipeline_events (entity_type, entity_id, event_type, data)
VALUES ('subscriber', $1::uuid, 'newsletter_digest_sent', jsonb_build_object('items', $2::int, 'subject', $3, 'sent_at', NOW()))
RETURNING id;

