const feed = $('2. Normalize Feed').item.json;
const countRow = $('4. Count Recipients').item.json;
return [{
  json: {
    status: 'sent',
    recipients: countRow.ready_count || 0,
    items: Array.isArray(feed.items) ? feed.items.length : 0,
    generated_at: feed.generated_at
  }
}];
