const input = items[0]?.json ?? {};
const { createHash, randomUUID } = await import('node:crypto');

const email = String(input.email ?? '').trim().toLowerCase();
const sourceCode = String(input.source_code ?? 'main_site').trim().toLowerCase() || 'main_site';
const fullName = input.full_name ? String(input.full_name).trim() : null;
const preferredLang = (input.preferred_lang ?? 'ru').toString().trim().toLowerCase() || 'ru';
const timezone = input.timezone ? String(input.timezone).trim() || null : null;
const consentVersion = input.consent_version ? String(input.consent_version).trim() : 'v1';
const doubleOptIn = input.double_opt_in !== false;

const acceptsMarketingRaw = input.accepts_marketing;
const acceptsMarketing = acceptsMarketingRaw === true || String(acceptsMarketingRaw ?? '').toLowerCase() === 'true' || ['yes','y','1','on'].includes(String(acceptsMarketingRaw ?? '').toLowerCase());
const acceptsTermsRaw = input.accepts_terms;
const acceptsTerms = acceptsTermsRaw === true || String(acceptsTermsRaw ?? '').toLowerCase() === 'true' || ['yes','y','1','on'].includes(String(acceptsTermsRaw ?? '').toLowerCase());

const errors = [];
if (!email) errors.push('email_required');
if (!acceptsMarketing) errors.push('marketing_consent_required');
if (!acceptsTerms) errors.push('terms_consent_required');

if (errors.length) {
  return [{
    json: {
      status: 'rejected',
      reason: 'validation_failed',
      errors,
      payload: input
    }
  }];
}

const unsubscribeToken = input.unsubscribe_token && String(input.unsubscribe_token).length >= 16
  ? String(input.unsubscribe_token)
  : randomUUID();

const dedupeKey = createHash('sha256').update(`${email}:${sourceCode}`).digest('hex');

return [{
  json: {
    status: 'accepted',
    email,
    full_name: fullName,
    preferred_lang: preferredLang || 'ru',
    timezone: timezone || null,
    source_code: sourceCode,
    consent_version: consentVersion,
    double_opt_in: doubleOptIn,
    unsubscribe_token: unsubscribeToken,
    dedupe_key: dedupeKey,
    raw_payload: input
  }
}];

const normalized = $('1. Normalize Payload').item.json;
return [{
  json: {
    status: normalized.status,
    reason: normalized.reason || 'validation_failed',
    errors: normalized.errors || [],
    payload: normalized.raw_payload || {}
  }
}];

