WITH requested AS (
  SELECT id FROM sources WHERE code = $1
),
fallback AS (
  SELECT id FROM sources WHERE code = 'main_site'
)
SELECT COALESCE((SELECT id FROM requested), (SELECT id FROM fallback)) AS source_id;

