const payload = $('1. Normalize Payload').item.json;
const sourceRow = $('2. Resolve Source').item.json;
const contactRow = $('3. Upsert Contact').item.json;

return [{
  json: {
    ...payload,
    contact_id: contactRow?.contact_id || null,
    source_id: sourceRow?.source_id || null,
    subscriber_status: payload.double_opt_in ? 'pending_opt_in' : 'subscribed'
  }
}];

const context = $('4. Assemble Context').item.json;
const subscriber = $json;

const pending = subscriber.status === 'pending_opt_in';
const wasPendingBefore = subscriber.previous_status === 'pending_opt_in';
const wasConfirmed = !!subscriber.previous_confirmed_at;
const isNew = subscriber.is_new === true;

return [{
  json: {
    ...context,
    subscriber_id: subscriber.id,
    subscriber_status: subscriber.status,
    unsubscribe_token: subscriber.unsubscribe_token,
    double_opt_in_required: subscriber.double_opt_in_required,
    confirmed_at: subscriber.confirmed_at,
    unsubscribed_at: subscriber.unsubscribed_at,
    previous_status: subscriber.previous_status,
    previous_confirmed_at: subscriber.previous_confirmed_at,
    is_new: isNew,
    should_send_doi: pending,
    should_send_welcome: !pending && (isNew || !wasConfirmed || wasPendingBefore)
  }
}];

const context = $('6. Decide Next Step').item.json;
    const template = $('8a. Load DOI Template').item.json || {};
    const baseUrl = $env.NEWSLETTER_CONFIRM_URL_BASE || 'https://example.com/newsletter/confirm';
    const confirmUrl = `${baseUrl}?token=${encodeURIComponent(context.unsubscribe_token)}&action=confirm`;

    const fullName = context.full_name ? String(context.full_name).trim() : '';
    const fullNameSuffix = fullName ? `, ${fullName}` : '';

    function applyTemplate(input, variables) {
      if (typeof input !== 'string') return '';
      return Object.entries(variables).reduce((acc, [key, value]) => {
        const safeValue = value == null ? '' : String(value);
        const pattern = new RegExp(`{{\s*${key}\s*}}`, 'g');
        return acc.replace(pattern, safeValue);
      }, input);
    }

    const subjectTemplate = template.subject_template || '';
    const bodyTemplate = template.body_template || '';
    if (!subjectTemplate || !bodyTemplate) {
      throw new Error('Newsletter DOI template is missing subject or body content.');
    }

    const subject = applyTemplate(subjectTemplate, {
      confirm_url: confirmUrl,
      full_name: fullName,
      full_name_suffix: fullNameSuffix,
    });

    const bodyText = applyTemplate(bodyTemplate, {
      confirm_url: confirmUrl,
      full_name: fullName,
      full_name_suffix: fullNameSuffix,
    });

    const bodyHtml = bodyText.replace(/
/g, '<br />');

    return [{
      json: {
        email: context.email,
        full_name: fullName || null,
        subject,
        body_text: bodyText,
        body_html: bodyHtml,
        confirm_url: confirmUrl,
        subscriber_id: context.subscriber_id,
        contact_id: context.contact_id,
        meta_kind: 'newsletter_doi'
      }
    }];

