const isGmail = !!$json.threadId && !!$json.internalDate;

function fromWebhook(j) {
  const body = j.body || {};
  const headers = j.headers || {};
  return {
    trigger: 'webhook',
    source_code: headers['x-resource'] || 'chain_do', // fallback
    form_code: headers['x-form-code'] || body.form_code || null,
    subject: body.subject || `Form: ${headers['x-form-code'] || 'unknown'}`,
    from_email: (body.email || '').toLowerCase().trim() || null,
    from_name: body.full_name || null,
    body_text: body.message || '',
    body_html: body.message_html || null,
    sent_at: new Date().toISOString(),
    external_message_id: body.external_id || null,
    attachments: body.attachments || [],
    raw: j
  };
}

function fromGmail(j) {
  // –£–ø—Ä–æ—â–µ–Ω–Ω—ã–π –ø–∞—Ä—Å–∏–Ω–≥ –ø–æ–ª—è "From", —á—Ç–æ–±—ã –¥–æ—Å—Ç–∞—Ç—å email –∏ –∏–º—è
  const fromRaw = j.From || '';
  const nameMatch = fromRaw.match(/^(.*)<.*>$/);
  const emailMatch = fromRaw.match(/<(.*)>/);

  const from_name = nameMatch ? nameMatch[1].trim() : (emailMatch ? '' : fromRaw);
  const from_email = emailMatch ? emailMatch[1].toLowerCase().trim() : fromRaw.toLowerCase().trim();

  return {
    trigger: 'gmail',
    source_code: 'email_inbox_hr',
    form_code: null,
    subject: j.Subject || '(no subject)',
    from_email: from_email || null,
    from_name: from_name || null,
    body_text: j.snippet || '', // Gmail Trigger –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –¥–∞–µ—Ç —Ç–æ–ª—å–∫–æ snippet. –î–ª—è –ø–æ–ª–Ω–æ–≥–æ —Ç–µ–∫—Å—Ç–∞ –Ω—É–∂–Ω–∞ –¥–æ–ø. –Ω–æ–¥–∞
    body_html: null, // –ê–Ω–∞–ª–æ–≥–∏—á–Ω–æ, –¥–ª—è HTML –Ω—É–∂–Ω–∞ –¥–æ–ø. –Ω–æ–¥–∞
    sent_at: new Date(parseInt(j.internalDate, 10)).toISOString(),
    external_message_id: j.id || null,
    attachments: (j.attachments || []).map(a => ({ filename: a.filename, mime: a.mimeType, size: a.size })),
    raw: j
  };
}

const env = isGmail ? fromGmail($json) : fromWebhook($json);

// –¥–æ–º–µ–Ω –¥–ª—è –∫–æ–º–ø–∞–Ω–∏–π, –µ—Å–ª–∏ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞
env.from_domain = (env.from_email && env.from_email.includes('@')) ? env.from_email.split('@')[1] : null;

env.ai_view = {
  trigger: env.trigger,
  source_code: env.source_code,
  form_code: env.form_code,
  subject: env.subject,
  from_email: env.from_email,
  from_name: env.from_name,
  body_text: env.body_text,
  body_html: env.body_html,
  sent_at: env.sent_at,
  external_message_id: env.external_message_id,
  from_domain: env.from_domain,
};

return [env];

// Node: 1c. Parse AI JSON (robust)
const rootText = typeof $json.text === 'string' ? $json.text : null;
const contentText = $json.content?.[0]?.text ?? null;
const rawText = (rootText ?? contentText ?? '').replace(/\u0000/g, '').trim();

const errors = [];
let parsed = null;

if (rawText) {
  const start = rawText.indexOf('{');
  const end = rawText.lastIndexOf('}');
  if (start === -1 || end === -1) {
    errors.push({ code: 'ai_missing_json', message: 'Claude response does not contain JSON object' });
  } else {
    const jsonStr = rawText.slice(start, end + 1);
    try {
      parsed = JSON.parse(jsonStr);
    } catch (error) {
      errors.push({ code: 'ai_json_parse_error', message: error.message });
    }
  }
} else {
  errors.push({ code: 'ai_empty_response', message: 'Claude returned empty response' });
}

const fallback = {
  classification: { label: 'spam', intent: 'generic', confidence: 0 },
  contact: {},
  company: {},
  message: {},
  project: {},
  candidate_profile: {},
  newsletter: { subscribe: false, unsubscribe: false },
  tags: [],
  external: {},
  shortSummaryForSales: null,
};

const result = parsed && typeof parsed === 'object' ? parsed : fallback;

if (!result.classification || typeof result.classification !== 'object') {
  result.classification = { label: 'spam', intent: 'generic', confidence: 0 };
}
if (!Array.isArray(result.tags)) {
  result.tags = [];
}

const aiErrors = Array.isArray(result.ai_errors) ? Array.from(result.ai_errors) : [];
result.ai_errors = [...aiErrors, ...errors];
result.ai_raw_text = rawText || null;

return [result];

// Node: 1d. Enforce Intake Policy (Code)

const env = $('0a. Build Envelope').item.json;
const ai  = $json || {};

ai.classification = ai.classification || { label: 'ham', intent: 'generic', confidence: 0 };

const ALLOWED_INTENTS = ['candidate'];
const CANDIDATE_FORMS = ['vacancy','hr_intake','hr_candidate'];

const intent = String(ai.classification.intent || 'generic').toLowerCase();
const formCode = (env.form_code || '').toLowerCase();

const isCandidateSource =
  (env.trigger === 'webhook' && formCode && CANDIDATE_FORMS.includes(formCode)) ||
  (env.trigger === 'gmail' && env.source_code === 'email_inbox_hr');

if (isCandidateSource && ai.classification.label === 'ham' && !ALLOWED_INTENTS.includes(intent)) {
  ai.classification.label = 'spam';
  ai.classification.intent = 'generic';
  ai.classification.confidence = Math.max(ai.classification.confidence || 0, 0.9);
  ai.tags = Array.isArray(ai.tags) ? Array.from(new Set([...ai.tags, 'policy_violation_non_candidate'])) : ['policy_violation_non_candidate'];
}

const email = (ai.contact?.email || env.from_email || '').toLowerCase().trim() || 'unknown';
ai.external = ai.external || {};
ai.external.message_id = ai.external.message_id || env.external_message_id || null;
ai.external.dedupe_key = `${email}:${env.source_code}`;

ai.message = ai.message || {};
if (!ai.message.subject) ai.message.subject = env.subject || null;
if (!ai.message.sent_at) ai.message.sent_at = new Date(env.sent_at || Date.now()).toISOString();

return [ai];

// Node: 1e. Validate & Shape (Code)

// === small, dependency-free hash ===
function fnv1a64(str) {
  let hash = 0xcbf29ce484222325n;        // FNV offset basis
  const prime = 0x100000001b3n;          // FNV prime
  for (let i = 0; i < str.length; i++) {
    hash ^= BigInt(str.charCodeAt(i));
    hash = (hash * prime) & 0xffffffffffffffffn; // 64-bit overflow
  }
  return hash.toString(16).padStart(16, '0');    // 16 hex chars (64-bit)
}

function normUrl(u) {
  if (!u) return null;
  try {
    const candidate = u.startsWith('http://') || u.startsWith('https://') ? u : `https://${u}`;
    return new URL(candidate).toString();
  } catch (error) {
    return null;
  }
}

function fromDomain(domain) {
  if (!domain) return null;
  const base = domain.replace(/^www\./, '');
  const name = base.split('.')[0];
  if (!name) return null;
  return {
    name: name.charAt(0).toUpperCase() + name.slice(1),
    website: `https://${base}`,
  };
}

function toNumber(value) {
  if (value === null || value === undefined) return null;
  const num = typeof value === 'number' ? value : parseFloat(String(value).replace(',', '.'));
  return Number.isFinite(num) ? Number(num) : null;
}

function titleCase(value) {
  if (!value) return null;
  return value
    .toString()
    .split(/\s|_|-/)
    .map((part) => part ? part.charAt(0).toUpperCase() + part.slice(1).toLowerCase() : '')
    .filter(Boolean)
    .join(' ');
}

const env = $('0a. Build Envelope').item.json;
const ai  = $('1d. Enforce Intake Policy').item.json;

const email = (ai?.contact?.email || env.from_email || '').toLowerCase().trim() || null;
if (!email) {
  const message = 'Email is required';
  const error = new Error(message);
  error.cause = { kind: 'missing_email', ai_errors: ai?.ai_errors || [] };
  throw error;
}

const form_code   = env.form_code || null;
const source_code = env.source_code || 'email_inbox';
const channel     = env.trigger === 'gmail' ? 'email' : 'form';

const msg       = ai?.message || {};
const subject   = msg.subject || env.subject || null;
const sent_at   = new Date(msg.sent_at || env.sent_at || Date.now()).toISOString();
const body_text = (msg.plain ?? env.body_text ?? '').toString();
const body_html = msg.html ?? env.body_html ?? null;

const rawStable = env.raw ? JSON.stringify(env.raw) : JSON.stringify({
  source_code, form_code, email, subject, body_text,
});
const sig = fnv1a64(rawStable);

const external_id =
  env.external_message_id
  || (form_code ? `form-${form_code}-${sig}`
                : `auto-${source_code}-${sig}`);

const aiCompany   = ai?.company || {};
const domCompany  = fromDomain(env.from_domain);
const company     = aiCompany?.name ? aiCompany : domCompany || null;

const classification = ai?.classification || {};
let lead_intent    = (classification.intent || 'generic').toLowerCase();
if (lead_intent === 'vacancy') {
  lead_intent = 'candidate';
}
const aiErrors = Array.isArray(ai?.ai_errors) ? ai.ai_errors.filter(Boolean) : [];

const intakeFlags = new Set(Array.isArray(ai?.tags) ? ai.tags.filter(Boolean) : []);
if (aiErrors.length) intakeFlags.add('ai_parse_warning');

const summary = ai?.shortSummaryForSales
  || (body_text ? `${body_text.slice(0, 260)}${body_text.length > 260 ? '‚Ä¶' : ''}` : null);

const candidateRaw = ai?.candidate_profile || {};
const rawSkills = Array.isArray(candidateRaw.skills) ? candidateRaw.skills : [];
const sanitizedSkills = rawSkills
  .map((skill) => typeof skill === 'string' ? skill.trim() : null)
  .filter(Boolean)
  .map((skill) => titleCase(skill));
const experienceYears = toNumber(candidateRaw.experience_years);
const rateMin = toNumber(candidateRaw.rate_min);
const rateMax = toNumber(candidateRaw.rate_max);
let rateCurrency = typeof candidateRaw.rate_currency === 'string'
  ? candidateRaw.rate_currency.trim().toUpperCase()
  : null;
const allowedCurrencies = new Set(['USD', 'EUR', 'PLN']);
if (!rateCurrency || !allowedCurrencies.has(rateCurrency)) {
  rateCurrency = null;
}
const allowedPeriods = new Set(['hourly', 'daily', 'monthly']);
const ratePeriod = typeof candidateRaw.rate_period === 'string' && allowedPeriods.has(candidateRaw.rate_period)
  ? candidateRaw.rate_period
  : 'hourly';
const allowedAvailability = new Set(['available_now', 'part_time', 'notice_period', 'unavailable']);
let availability = typeof candidateRaw.availability === 'string'
  ? candidateRaw.availability
  : null;
if (!availability || !allowedAvailability.has(availability)) {
  availability = 'notice_period';
}

const candidateProfile = {
  cv_url: normUrl(candidateRaw.cv_url || null),
  skills: sanitizedSkills,
  experience_years: experienceYears,
  location: candidateRaw.location ? candidateRaw.location.toString().trim() : null,
  rate_currency: rateCurrency,
  rate_min: rateMin,
  rate_max: rateMax,
  rate_period: ratePeriod,
  availability,
};

const seniority = experienceYears !== null
  ? (experienceYears >= 5 ? 'Senior' : experienceYears >= 2 ? 'Middle' : 'Junior')
  : 'Middle';
const defaultStatus = availability === 'available_now' ? 'active' : 'prospect';
const poolTargets = [];

const primarySkill = sanitizedSkills[0] || null;
if (primarySkill) {
  poolTargets.push({
    name: `${primarySkill} ${seniority}`.replace(/\s+/g, ' ').trim(),
    status: defaultStatus,
  });
}
const tagDerived = Array.from(intakeFlags).find((tag) => /react|solidity|python|node|go|java|design|ux|qa/i.test(tag));
if (tagDerived) {
  const normalized = titleCase(tagDerived.replace(/[_-]/g, ' '));
  if (normalized && !poolTargets.some((p) => p.name === `${normalized} ${seniority}`)) {
    poolTargets.push({
      name: `${normalized} ${seniority}`.replace(/\s+/g, ' ').trim(),
      status: defaultStatus,
    });
  }
}
if (!poolTargets.length) {
  poolTargets.push({
    name: `${seniority} Talent Pool`,
    status: defaultStatus,
  });
}

if (lead_intent === 'candidate') {
  if (!candidateProfile.cv_url) intakeFlags.add('candidate_missing_cv');
  if (!sanitizedSkills.length) intakeFlags.add('candidate_missing_skills');
}

return [{
  form_code,
  source_code,
  raw_payload: env.raw,
  email,
  full_name: ai?.contact?.full_name || env.from_name || '',
  message: body_text,
  external_id,

  classification,

  channel,
  subject,
  sent_at,
  body_html,

  company_name: company?.name || null,
  company_website: normUrl(company?.website || null),
  preferred_lang: ai?.contact?.preferred_lang || null,
  timezone: ai?.contact?.timezone || null,
  title: ai?.contact?.title || null,

  lead_intent,
  attachments: env.attachments || [],
  dedupe_key: `${email}:${source_code}`,
  sales_summary: summary,
  ai_errors: aiErrors,
  ai_raw_text: ai?.ai_raw_text || null,
  intake_flags: Array.from(intakeFlags),
  classification_source: aiErrors.length ? 'llm_with_warnings' : 'llm',
  candidate_profile: candidateProfile,
  candidate_pool_targets: poolTargets,
}];

