parameters.jsCode:
const intake = $('1e. Validate & Shape').item.json;
const lead = $('6. Upsert Lead').item.json;
const contact = $('5. Merge Contact Result').item.json;
const summary = intake.sales_summary || (intake.message ? `${intake.message.slice(0, 200)}${intake.message.length > 200 ? '‚Ä¶' : ''}` : '–ù–µ—Ç –∫–æ—Ä–æ—Ç–∫–æ–≥–æ —Ä–µ–∑—é–º–µ ‚Äî –ø—Ä–æ–≤–µ—Ä—å—Ç–µ –¥–∏–∞–ª–æ–≥.');

const flags = new Set(intake.intake_flags || []);
const aiErrors = Array.isArray(intake.ai_errors) ? intake.ai_errors.filter(Boolean) : [];
if (aiErrors.length) {
  flags.add('ai_warning');
}

const lines = [];
lines.push('üë§ –ù–æ–≤—ã–π –∫–∞–Ω–¥–∏–¥–∞—Ç –æ—Ç–∫–ª–∏–∫–Ω—É–ª—Å—è –Ω–∞ –≤–∞–∫–∞–Ω—Å–∏—é');
lines.push(`Lead ID: ${lead.lead_id}`);
lines.push(`Candidate: ${(contact.full_name || intake.full_name || '‚Äî')} <${intake.email}>`);
lines.push(`Intent: ${intake.lead_intent} | Classification: ${(intake.classification?.label || 'n/a')}`);
lines.push('‚Äî');
lines.push(summary);

if (flags.size) {
  lines.push('‚Äî');
  lines.push(`‚ö†Ô∏è Flags: ${Array.from(flags).join(', ')}`);
}
if (aiErrors.length) {
  const preview = aiErrors.map(e => e?.code || 'error').join(', ');
  lines.push(`‚ö†Ô∏è AI warnings: ${preview}`);
}

return [{ text: lines.join('\n') }];

const intake = $('1e. Validate & Shape').item.json;
const lead = $('6. Upsert Lead').item.json;
const contact = $('5. Merge Contact Result').item.json;
const company = $('Merge company data').item.json;
const conversation = $('7a. Start Conversation').item.json;
const submission = $('2. Insert Submission').item.json;

return [{
  lead_id: lead.lead_id,
  contact_id: contact.contact_id,
  company_id: company.company_id,
  conversation_id: conversation.conversation_id,
  message_id: $('7b. Insert First Message').item.json.message_id,
  email: intake.email,
  full_name: intake.full_name,
  preferred_lang: intake.preferred_lang || 'ru',
  timezone: intake.timezone,
  lead_intent: intake.lead_intent,
  classification_label: intake.classification?.label || null,
  classification_confidence: intake.classification?.confidence ?? null,
  sales_summary: intake.sales_summary,
  dedupe_key: intake.dedupe_key,
  source_code: intake.source_code,
  form_code: intake.form_code,
  submission_id: submission.submission_id,
  ai_errors: intake.ai_errors || [],
  intake_flags: intake.intake_flags || [],
  ai_raw_text: intake.ai_raw_text,
  candidate_profile: intake.candidate_profile || {},
  candidate_pool_targets: intake.candidate_pool_targets || []
}];
